import React from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'
import axios from 'axios'
import moment from 'moment'
function CreateRequest (){

    const initialValues = {
        cnic:"",
        bloodGroup:"",
        QtyNeed:"",
        Date : "",
        Time : "",
        Reason:"",
        TimePeriod:"",
    
    }
    const validationSchema = Yup.object().shape({
        cnic : Yup.number().required('The cnic is required'),
        bloodGroup: Yup.string().required('The blood group is required'),
        QtyNeed: Yup.number().required('The qty is required'),
        Date : Yup.string().required('The date is required'),
        // .max(new Date(), 'You cant be be born in a future'),
        Time: Yup.string().required('the time is required'),
        TimePeriod : Yup.number().required('The time period is required'),
        Reason: Yup.string().min(10).required('The reason is required'),
    });
    // {cnic : cnic, bloodGroup: bloodGroup, QtyNeed: QtyNeed, Date : Date, Time : Time, TimePeriod : TimePeriod, Reason : Reason 
    const onSubmit = (data)=>{
        axios.post('http://localhost:3001/api/CreateRequest/create', data).then((response)=>{
            console.log(data);
        })
    }
    return (
        
        <div className="createPostPage">

        <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>

        <Form className="formContainer">
            
        <label>Cnic: </label>
        <ErrorMessage name="cnic" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="cnic" placeholder="eg: A+"/>
       
        <label>Blood Group:</label>
        <ErrorMessage name="bloodGroup" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="bloodGroup" placeholder="eg: A+"/>
        
        <label>Qty Need:</label>
        <ErrorMessage name="QtyNeed" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="QtyNeed" placeholder="eg: 1" />
        
        <label>Date:</label>
        <ErrorMessage name="Date" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="Date" placeholder="eg: A+"/>

        
        <label>Time:</label>
        <ErrorMessage name="Time" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="Time" placeholder="eg: A+"/>

        <label>Time Period:</label>
        <ErrorMessage name="TimePeriod" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" name="TimePeriod" placeholder="eg: 1 day"/>


        <label>Reason:</label>
        <ErrorMessage name="Reason" component="span"/>
        <Field autoComplete = "off" id="inputCreatePost" className="post" name="Reason" placeholder="eg: my friend injured in a bike accident "/>

        <button type="submit">Post</button>




        </Form>

        </Formik>

        </div> 
    );
    
}
export default CreateRequest;
