import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'

function Donate(){
    
    const[bankNameList, setBankNameList] = useState([])
    const[bankName, setBankName] = useState({
        bankName: '',
        req_id: '',

    })
 


   
        function getBank(){
       
        axios.get('http://localhost:3001/api/Donation/donate', {
            headers:{
                accessToken2: sessionStorage.getItem('accessToken2'),
            }
        }).then((response)=>{
            if(response.data.error){
                console.log('error in donate');
                toast.error(response.data.error)
            }else{
                console.log(response.data);
                setBankNameList(response.data)
                // toast.success(response.data)
            }
            
        })
       
    }

const handleChange = (event)=>{
            const newdata = {...bankName}
            newdata[event.target.id] =event.target.value 
            setBankName(newdata)
            console.log(newdata);
       

}
function Submit(event){
    event.preventDefault()
  
    
    // add the submit api
    axios.post('http://localhost:3001/api/Donation/postdonate',{
        bankName: bankName,
        req_id : reqId,
    },{
        headers:{
            accessToken2: sessionStorage.getItem('accessToken2'),
        }
    }).then((response)=>{
        if(response.data.error){
            toast.error(response.data.error)
        }else{
            console.log('donate success');
        }
    })
    
    
}

     // function ends
    useEffect(()=>{

        getBank()
    },[])
    return (
        <form onSubmit={(e)=>{Submit(e)}}>
            <h1>Enter Requirement ID: </h1>
            <input type="text" id="req_id" value={bankName.req_id} onChange={handleChange}/>
                <h1>Choose Blood Bank</h1>
                <select value={bankName.bankName}   onChange={handleChange}>
                 <option value="" >Choose Blood Bank</option>
                { 
                        bankNameList.map((val, key)=>{
                        return(
                            <option value={val.BankName} id={key+1}> {val.BankName}</option>
                                )
                            }
                            )
                    }
                               
            </select>
           <button type="submit">Submit</button>
        </form>
    );


}

export default Donate;
