import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";


function GetDonations (){

    const [requestList, setRequestList] = useState([])
    const [requestList2, setRequestList2] = useState([])
    const [requestList3, setRequestList3] = useState([])
    const [requestList4, setRequestList4] = useState([])
    const [requestList5, setRequestList5] = useState([])
    
    const [requestList6, setRequestList6] = useState([])
    const [requestList7, setRequestList7] = useState([])


    const showRequest = ()=>{
        axios.get('http://localhost:3001/api/Donation/getDonations', {
            headers:{
                accessToken2 : sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
                
            }else{
            
           setRequestList(response.data);
           console.log(requestList);
                 }
        })
        
    }
    const getBankinfo = (bankname)=>{
        const Bank = bankname;
        console.log(bankname);
            axios.get(`http://localhost:3001/api/Blood/bloodbank/${bankname}`,{
                headers:{
                    accessToken2: sessionStorage.getItem("accessToken2")
                }
            }).then((response) => {
                if(response.data.error){
                    toast.error(response.data.error);
                }else{
                    console.log('get bank main info');
                    setRequestList2(response.data)

                    // bank location 

                    const getBankloc = ()=>{
                        
                            axios.get(`http://localhost:3001/api/Blood/bloodbankloc/${Bank}`,{
                                headers:{
                                    accessToken2: sessionStorage.getItem("accessToken2")
                                }
                            }).then((response) => {
                                if(response.data.error){
                                    toast.error(response.data.error);
                                }else{
                                    console.log('get bank main loc');
                                    setRequestList3(response.data)
                                    

                                const getBankContact = ()=>{
                                    console.log(Bank);
                                    console.log('conatct me');
                                    axios.get(`http://localhost:3001/api/Blood/bloodbankcontact/${Bank}`,{
                                        headers:{
                                            accessToken2: sessionStorage.getItem("accessToken2")
                                        }
                                    }).then((response) => {
                                        if(response.data.error){
                                            toast.error(response.data.error);
                                        }else{
                                            console.log('get bank main contact');
                                            setRequestList4(response.data)
                                        }
                                    })
                                }// contact funtion ends //
                                getBankContact()

                                                                       
                                }
                               
                                
                                
                            })
                            
                    }
                    getBankloc();
                    
                              

                }
            })
    }

    
    // person info 
    const personinfo =(cnic, loc_id) =>{
        const Cnic = cnic;
        console.log(cnic);
        axios.get(`http://localhost:3001/api/Person/personinfo1/${cnic}`, {
            headers:{
                accessToken2 : sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
                
            }else{
            
           setRequestList5(response.data);
           const show2 = ()=>{
            axios.get(`http://localhost:3001/api/Person/personcontact1/${cnic}`, {
                headers:{
                    accessToken2 : sessionStorage.getItem("accessToken2"),
                }
            }).then((response)=>{
                if(response.data.error){
                    toast.error('You are not authenticated to use this feature')
                    
                }else{
                
               setRequestList6(response.data);
                }// else 2 ends

            })
        }// end function2

            show2()

            }// else

        })
    }// ends

    return (

        <div>
            <div id="main1">
            <div id="heading">
                <h1 >Donations</h1>
            </div>
            <button id="reqbtn" onClick={showRequest}><h3>Find</h3></button>
            
            </div>
        {
            requestList.map((val,key)=>{
                return (
                    
                    <div className="requests">
                        
                        <div>
                            <div className="main">
                            <h1>Donor : {val.cnic}</h1>
                            <h1 id="reqid">Requirement id: {val.req_id}</h1>
                           
                            
                            </div>
                            <button id="btn-del" onClick={()=>{personinfo(val.cnic)}}>Info</button>
                            {
                                requestList5.map((val5, key)=>{
                                    return(
                                        
                                      <div>
                                          <h3>Name: {val5.fname + " " + val5.lname}</h3>
                                        <h3>Weight: {val5.weight}</h3>
                                      </div>  
                                    );
                                })
                            }
                            {
                                requestList6.map((val6, key)=>{
                                    return(
                                      <div>
                                          <h3>Contact no {key+1} : {val6.contactno}</h3>
                                        
                                      </div>  
                                    );
                                })
                            }
                            <h3>Blood Group: {val.bloodGroup}</h3>
                            <h3>Posted At: {val.Date}</h3>
                            <h3>Time: {val.Time}</h3>
                            <h1>Bloodbank : {val.bankName}</h1>
                            <button id="btn-del" onClick={()=>{getBankinfo(val.bankName)}}>Info</button>
                            {
                                requestList2.map((val2, key)=>{
                                    return (
                                        <div>
                                    <h3>OpeningTimings : {val2.openingTimings}</h3>
                                    <h3>ClosingTimings : {val2.closingTimings}</h3>
                                    </div>
                                    );
                                })
                            }
                            {
                                requestList3.map((val3, key)=>{
                                    return(
                                        <div>
                                    <h3>Location Branch {key+1} : {"Plot " + val3.Plot  + ", Block : " + val3.Block + ", " +"Street : "+ val3.Street +" , " + val3.Area + " , " + val3.City}</h3>
                                    <h3>Manager Branch {key+1} : {val3.Manager_fname + " " + val3.Manager_lname}</h3>

                                        </div>
                                    );
                                })
                            }
                            {
                                requestList4.map((val4, key)=>{
                                    return(
                                        <div>
                                    <h3>Contact Number  {key+1} : {val4.contactno }</h3>

                                        </div>
                                    );
                                })
                            }
                           


                        </div>

                    </div>
                );
            })
        }
       
        </div>
    );




}

export default GetDonations;
