import React, {useEffect, useState} from 'react';
import {AppBar, Container, Box, Toolbar, Typography, Button, IconButton} from '@mui/material';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import {Link, useHistory} from 'react-router-dom';
import LoginP from '../LoginUser/LoginP';
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
  }
  

const UserHome = () => {
    const navigate = useHistory();
    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
      );
    
      useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
      
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
      }, []);

    return(

        <Box  sx={{ flexGrow: 1 }}>
   
        <AppBar style={{backgroundColor:'crimson'}} position="static">
        
          <Toolbar>
          <IconButton
              size="large"
              edge="false"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={()=>{
                navigate.push('/');
              }}
           
            >
              <HomeRoundedIcon />
            </IconButton>
            <Button style={{marginLeft: windowDimensions.width <= 1058 ? '40px' : '250px', textDecoration: 'underline'}} component={Link} to='/loginP' color="inherit" >Login</Button>
            </Toolbar>
        </AppBar> 

        <Container>
              <LoginP/>
        </Container>
        
        </Box>

        
    )
}

export default UserHome;
