import React from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'
import axios from 'axios'
import { toast } from "react-toastify";
import useStyles from './styles';
import { Typography } from "@mui/material";

function CreateRequest (){
    const classes  = useStyles();
    const user =  sessionStorage.getItem('profile');

    const initialValues = {
      
        bloodGroup:"",
        QtyNeed:"",
        Reason:"",
        TimePeriod:"",
    
    }
    // const validationSchema = Yup.object().shape({
    //     // cnic : Yup.number().required('The cnic is required'),
    //     bloodGroup: Yup.string().required('The blood group is required'),
    //     QtyNeed: Yup.number().required('The qty is required'),
    //     // Date : Yup.string().required('The date is required'),
    //     // .max(new Date(), 'You cant be be born in a future'),
    //     // Time: Yup.string().required('the time is required'),
    //     TimePeriod : Yup.number().required('The time period is required'),
    //     Reason: Yup.string().min(10).required('The reason is required'),
    // });
    // {cnic : cnic, bloodGroup: bloodGroup, QtyNeed: QtyNeed, Date : Date, Time : Time, TimePeriod : TimePeriod, Reason : Reason 
    const handleSubmit = (data)=>{
        axios.post('http://localhost:3001/api/Request/create', data,{
            headers:{
                accessToken2 : sessionStorage.getItem('accessToken2'),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('Request is not submitted')
            }else{
            console.log(data);
            toast.success('Post is submitted successfully')
            }
        })
    }
    return (
        
        <Paper className={classes.paper} elevation={10}>
            <form autoComplete="off" validate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
            <Typography variant="h6">{user ? 'Editing' : 'Creating'} a Post</Typography>

            </form>

        </Paper>
    
    );
    
}
export default CreateRequest;
