// this will return the current users posts 

import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import React, {useEffect, useState} from "react";
import { toast } from "react-toastify";
import Post from "./PostDetail/Post";
import {AppBar, Grid, IconButton, Toolbar, Typography} from '@mui/material';
import HomeRoundedIconButton from '@mui/icons-material/HomeRounded';
import useStyles from './styles';
function Myrequests(){

    // let history = useHistory()
    const classes = useStyles();
    const [requestList, setRequestList] = useState([])
  
    useEffect(()=>{
        axios.get("http://localhost:3001/api/Request/myrequests", {
            headers:{
                accessToken2: sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
            }if(response.data.err){
                toast.error('No post exist')
            }
            else if(response.data){
                console.log(response.data);
                setRequestList(response.data)
            }
        })

    }, [])  // inner function ends 
    const onDel = (req_id)=>{
        axios.delete(`http://localhost:3001/api/Request/delete/${req_id}`, {
            headers:{
                accessToken2: sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('Post is not deleted successfully')
            }else{
                console.log("deleted successfully");
                toast.success("Post deleted successfully")

            }
        })
    }
    return (

        <Box  sx={{ flexGrow: 1 }}>
   
    <AppBar style={{backgroundColor:'crimson'}} position="static">
    
      <Toolbar>
      <IconButton
          size="large"
          edge="false"
          color="inherit"
          aria-label="menu"
          sx={{ mr: 2 }}
          onClick={()=>{
            navigate.push('/user');
          }}
       
        >
          <HomeRoundedIcon />
        </IconButton>
        <Typography variant="h1" >Personal Details</Typography>
        
        </Toolbar>
       
    </AppBar> 

        <Grid className={classes.mainContainer} container alignItems="stretch" spacing={3}>       
        {
            requestList.map((post) => (
                <Grid key={post?.req_id} item xs={12} sm={12} md={6} lg={3}>
                  <Post  post={post}/>
                </Grid>
            ))
        }
       </Grid>

       </Box>
    );


}

export default Myrequests;
