import React, {startTransition, useState} from "react";
import axios from 'axios';
import {toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {useHistory} from "react-router-dom";
function Login (){
    const [bankName, setBankName] = useState("");
    const [password, setPassword] = useState("");
    let history =  useHistory();
    const login = ()=>{
        const data = {BankName:bankName, password: password}
        if(!data.BankName || !data.password){
            toast.error('Please Provide each field')
        }else{
        axios.post("http://localhost:3001/api/authBank/login", data).then((response)=>{
            if(response.data.error){
              
                // history.push("/Error")
                toast.error('Please enter a valid credentials')
                function start(){
                const timeoutStart = setTimeout(()=>{
                   history.push("/login")
                }, 3000)
            }
                start()
            }else{
                console.log(response.data)
                sessionStorage.setItem("accessToken",response.data);
            history.push("/")
      

            }
        
        })
    }
    }
    return ( 
    <div className="loginContainer">
        <div className="header">Login as Blood Bank</div>
        <div className="labels">
        <label>Blood Bank</label>
        <input type="text" onChange={(event)=>{
            setBankName(event.target.value);
        }}/>
        <label>Password: </label>
        <input type="password" onChange={(event)=>{
            setPassword(event.target.value);
        }}/>
         </div>
        <button onClick={login}>Login</button>
       
    </div>);
}

export default Login;
