import React from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'
function CreateRequest (){

    const initialValues = {
        bloodgroup:"",
        qty:"",
        timeperiod:"",
        reason:""
    }
    const validationSchema = Yup.object().shape({
        bloodgroup: Yup.string().required('The blood group should be add'),
        qty: Yup.number().required('The qty should be added'),
        timeperiod : Yup.number().required('The time period should be included'),
        reason: Yup.string().min(10).required('The reason is required'),
    });
    const onSubmit = (data)=>{
        console.log(data);
    }
    return (
        
        <div className="createPostPage">

        <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>

        <Form className="formContainer">
       
        <label>Blood Group:</label>
        <ErrorMessage name="bloodgroup" component="span"/>
        <Field autocomplete = "off" id="inputCreatePost" name="bloodgroup" placeholder="eg: A+"/>
        
        <label>Qty Need:</label>
        <ErrorMessage name="qty" component="span"/>
        <Field autocomplete = "off" id="inputCreatePost" name="qty" placeholder="eg: 1"/>

        <label>Time Period:</label>
        <ErrorMessage name="timeperiod" component="span"/>
        <Field autocomplete = "off" id="inputCreatePost" name="timeperiod" placeholder="eg: 1 day"/>


        <label>Reason:</label>
        <ErrorMessage name="reason" component="span"/>
        <Field autocomplete = "off" id="inputCreatePost" classname="post" name="reason" placeholder="eg: my friend injured in a bike accident "/>

        <button type="submit">Post</button>




        </Form>

        </Formik>

        </div> 
    );
    
}
export default CreateRequest;
