import React, { useState } from "react";
import axios from "axios";


function ShowRequests (){

    const [requestList, setRequestList] = useState([])


    const showRequest = ()=>{
        axios.get('http://localhost:3001/api/Request/requests').then((response)=>{
           setRequestList(response.data);
        })
    }
    return (

        <div>
            <button onClick={showRequest}>Request</button>
        {
            requestList.map((val,key)=>{
                return (
                    <div>
                        <h1>All Blood Requests</h1>
                        <div>
                            <h1>Cnic : {val.cnic}</h1>
                            <h2>Requirement id: {val.req_id}</h2>
                            <h3>Blood Group: {val.bloodGroup}</h3>
                            <h3>Requirement id: {val.QtyNeed}</h3>
                            <h3>Posted At: {val.Date}</h3>
                            <h3>Time: {val.Time}</h3>
                            <h3>Reason: {val.Reason}</h3>
                            <h3>Time Period : {val.TimePeriod}</h3>


                        </div>

                    </div>
                );
            })
        }
       
        </div>
    );




}

export default ShowRequests;
