import axios from "axios";
import React, {useEffect, useState, useHistory} from "react";
import { toast } from "react-toastify";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'

function Donate(){
    
    const[bankNameList, setBankNameList] = useState([])
      const [requestList, setRequestList] = useState([])
      let history = useHistory()

 
    const[bankName, setBankName] = useState({
        bankName: '',

    })

    const showRequest = ()=>{
        axios.get('http://localhost:3001/api/Request/requests', {
            headers:{
                accessToken : sessionStorage.getItem("accessToken"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
                
            }else{
            
           setRequestList(response.data);


       
        }
        })
    }
        function getBank(){
       
        axios.get('http://localhost:3001/api/Donation/donate', {
            headers:{
                accessToken2: sessionStorage.getItem('accessToken2'),
            }
        }).then((response)=>{
            if(response.data.error){
                console.log('error in donate');
                toast.error(response.data.error)
            }else{
                console.log(response.data);
                setBankNameList(response.data)
                // toast.success(response.data)
            }
            
        })
       
    }
     // function ends
    useEffect(()=>{
        showRequest()
        getBank()
    },[])
    return (

        <div>
            <div id="main1">
            <div id="heading">
                <h1 >Blood Requests</h1>
            </div>
            <button id="reqbtn" onClick={showRequest}><h3>Find</h3></button>
            
            </div>
        {
            requestList.map((val,key)=>{
                return (
                    
                    <div className="requests">
                        
                        <div>
                            <div className="main">
                            <h1>Cnic : {val.cnic}</h1>
                            <h1 id="reqid">Requirement id: {val.req_id}</h1>
                            
                            </div>
                            <h3>Blood Group: {val.bloodGroup}</h3>
                            <h3>Quantity Needed: {val.Qtyneed}</h3>
                            <h3>Posted At: {val.Date}</h3>
                            <h3>Time: {val.Time}</h3>
                            <h3>Reason: {val.Reason}</h3>
                            <h3>Time Period : {val.TimePeriod}</h3>
                            <form >
                                    <h1>Choose Blood Bank</h1>
                                    <select value={bankName}>
                                        <option value="" >Choose Blood Bank</option>
                        
                                
                                    
                                  { 
                                   bankNameList.map((val, key)=>{
                                    return(
                                    <option value={val.BankName} id={key+1}> {val.BankName}</option>
                                    )
                                   })
                                }
                               
                            </select>
                            <button type="submit">Submit</button>
                            </form>
                            <button id="btn-del">Donate</button>
                                        
                                  
                            
                        
                        
                            

                            


                        </div>

                    </div>
                );
            })
        }
       
        </div>
    );









    


   

