import React, { useState, useEffect } from "react";
import axios from 'axios';
import {Button,  AppBar, Toolbar, Box, IconButton, Container, Typography, TextField} from '@mui/material';
import { useHistory, Link } from "react-router-dom";
import { toast } from "react-toastify";
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import myimage from '../../images/donationImage.png';

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
}

function LoginP() {
    const [cnic, setCnic] = useState("");
    const [password, setPassword] = useState("");
    let history = useHistory();

    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
    );

    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }

        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, []);

    const login = () => {
        const data = { cnic: cnic, password: password }
        if (!data.cnic || !data.password) {
            toast.error('Please provide each field')
        }
        axios.post("http://localhost:3001/api/authPerson/LoginP", data).then((response) => {
            if (response.data.error) {
                alert(response.data.error);
                history.push("/Error")
                function start() {
                    const timeoutStart = setTimeout(() => {
                        history.push("/loginP")
                    }, 3000)
                }
                start()



            } else {
                console.log(response.data)
                toast.success('Successfully logged in')
                sessionStorage.setItem("accessToken2", response.data)


                // history.push("/homeuser")
                // after login go to user access functionalities page 
                history.push("/")


            }

        })
    }
    return (

        <Box sx={{ flexGrow: 1 }}>

            <AppBar style={{ backgroundColor: 'crimson' }} position="static">

                <Toolbar>
                    <IconButton
                        size="large"
                        edge="false"
                        color="inherit"
                        aria-label="menu"
                        sx={{ mr: 2 }}
                        onClick={() => {
                            history.push('/');
                        }}
                
                    >
                        <HomeRoundedIcon />
                    </IconButton>
                        <Typography variant="h4">
                            Bloodu Login
                        </Typography>
                    </Toolbar>
            </AppBar>
            <Container  style={{display: windowDimensions.width >= 850 && 'flex', justifyContent:  windowDimensions.width >= 850 && 'space-evenly'}}>
        <img style={{ marginRight:'40px', marginTop:'20px', width:  windowDimensions.width >= 850 ? 'auto' : '100%', height: windowDimensions.width >= 850 ? '500px' : '700px'}} src={myimage} alt="donation" />
                
            <div style={{marginTop: '50px', marginLeft:'100px'}}>
                <TextField
                    style={{height:'100px', width:'500px'}}
                    name="cnic"
                    variant="outlined"
                    label="CNIC"
                    fullWidth
                    onChange={(e)=>setCnic(e.target.value)}

                />

                <TextField
                    style={{height:'100px', width:'500px'}}
                    name="password"
                    variant="outlined"
                    label="Password"
                    fullWidth
                    type="password"
                    onChange={(e)=>setCnic(e.target.value)}

                />
               
                
                
              
                <Button  variant="contained" style={{marginTop:'10px',marginRight:'80px', backgroundColor:'crimson'}} onClick={login}>Login</Button>
          
                </div>
    </Container>
    
    </Box> 
    
    )
}

export default LoginP;
