import React, {useEffect, useState} from 'react';
import {AppBar, Container, Box, Toolbar, Typography, Button, IconButton} from '@mui/material';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import {Link, useHistory} from 'react-router-dom';
import registerP from '../RegisterUser/RegisterP';
import video from '../../assets/video.mp4';
import RegisterP from '../RegisterUser/RegisterP';
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
  }
  

const UserHome = () => {
    const navigate = useHistory();
    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
      );
    
      useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
      
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
      }, []);

    return(

        <Box  sx={{ flexGrow: 1 }}>
   
        <AppBar style={{backgroundColor:'crimson'}} position="static">
        
          <Toolbar>
          <IconButton
              size="large"
              edge="false"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={()=>{
                navigate.push('/');
              }}
           
            >
              <HomeRoundedIcon />
            </IconButton>
            <Button style={{marginLeft: windowDimensions.width <= 1058 ? '40px' : '250px', textDecoration: 'underline'}} component={Link} to='/loginP' color="inherit" >Login</Button>
    
            </Toolbar>
        </AppBar> 

        <Container maxWidth='xl' style={{ marginTop: '40px', display: windowDimensions.width >= 1041 && 'flex', justify: windowDimensions.width >= 1041 && 'space-evenly' }}>
            <Container style={{ marginLeft: '0px', marginTop: '20px', height: windowDimensions.width >= 1041 && '500px', width: windowDimensions.width >= 1041 ? '500px' : '100%' }} >
                <video src={video} controls />
            </Container>
            <Container style={{ marginLeft: windowDimensions.width >= 1041 ? '100px' : '0px', marginTop: '50px' }}>
                <Typography style={{ marginTop: '5px', fontFamily: 'Arial Nova', fontSize: '20px' }}>
                    <RegisterP/>
                </Typography>
            </Container>

        </Container>

        
        </Box>

        
    )
}

export default UserHome;
