import React, { useState, useEffect } from "react";
import {useFormik,  Formik, Form, Field, ErrorMessage } from 'formik'
import * as Yup from 'yup'
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import { Alert, Stack , AppBar, Container, Button, IconButton, Box, Toolbar, Typography, TextField } from "@mui/material";
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import myimage from '../../images/donationImage.png';
import useStyles from './styles'
import {red} from '@mui/material/colors'
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
}
// cnic, password, fname, lname, blood_group, weight, frequencyDonation, IsDiabetes, IsHypetitus, contactno,  postalCode, Area, Block, City
const initialValues = {
    cnic: "",
    password: "",
    fname: "",
    lname: "",
    blood_group: "",
    weight: "",
    frequencyDonation: "",
    IsDiabetes: "",
    IsHypetitus: "",
    contactno: "",
    postalCode: "",
    Area: "",
    Block: "",
    City: "",
    // file:'',


}
const Schema = Yup.object().shape({

    cnic: Yup.number().required('The cnic is required must be 13 digits'),
    password: Yup.string().min(10).required('The password is required'),
    fname: Yup.string().required('The first name is required'),
    lname: Yup.string().required('The last name is required'),
    blood_group: Yup.string().min(2).max(2).required('the blood group is required'),
    weight: Yup.number().required('the weight is required 11 digits'),
    frequencyDonation: Yup.number().required('The frequency donation is required'),
    IsDiabetes: Yup.string().required('This is the required field'),
    IsHypetitus: Yup.string().required('This is the required field'),
    contactno: Yup.string().required('The contact number is required'),
    postalCode: Yup.string().required('The postal code is required'),
    Area: Yup.string().required('The area is rquired'),
    Block: Yup.string().required('The block is required'),
    City: Yup.string().required('The city is required'),
    

});
function RegisterP() {
    let history = useHistory();
    const classes = useStyles();
    const [error, setError] = useState("");
    const [alert , setAlert] = useState(false);
    const [alertdata, setAlertdata] = useState("");

    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
    );

    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }

        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, []);

   const formik =  useFormik({
        initialValues : initialValues,
        validationSchema: Schema,
        onSubmit : (values) => {

            console.log("submit");
                   console.log(values);
    
            // axios.post("http://localhost:3001/api/authPerson/RegisterP", data).then((response) => {
    
            //     if (response.data.error) {
            //         setAlert(true);
            //         setAlertdata(true);
    
            //     } else {
            //         // if success 
            //         console.log(response.data);
            //         function start() {
            //             const now = setTimeout(() => {
            //                 history.push("/loginP")
            //             }, 3000)
            //         }
            //         start()
            //     }
    
    
    
            // })
    
    
    
        }
    })
    
    
    
    const handleScroll = () => {
        const targetPosition = 800;
        window.scrollTo({top: targetPosition, behavior: "smooth"});
    }
    return (

        <Box sx={{ flexGrow: 1 }}>
            <AppBar style={{ backgroundColor: 'crimson' }} position="static">

                <Toolbar>
                    <IconButton
                        size="large"
                        color="inherit"
                        aria-label="menu"
                        sx={{ mr: 2 }}
                        onClick={() => {
                            history.push('/user');
                        }}

                    >
                        <HomeRoundedIcon />
                    </IconButton>
                    <Typography variant="h6">Register Now to Save Life</Typography>
                </Toolbar>
            </AppBar>
            <Container style={{ display: windowDimensions.width >= 850 && 'flex', justifyContent: windowDimensions.width >= 850 && 'space-evenly' }}>
                <div style={{position:'relative'}}>
                <img style={{ marginRight: '40px', marginTop: '20px', width: windowDimensions.width >= 850 ? 'auto' : '100%', height: windowDimensions.width >= 850 ? '500px' : '700px' }} src={myimage} alt="donation" />
                </div>
                {windowDimensions.width <= 850 && (
                   <IconButton style={{ position: "absolute", top: 580, right: 0, zIndex: 1}} onClick={handleScroll}>
                    <KeyboardDoubleArrowDownIcon style={{height:'50px', width:'50px'}} />
                    </IconButton>
                   
                )}  
            
                {alert && (
               
               <Stack sx={{width:'100%', marginBottom:'20px'}}>
               <Alert onClose={()=>{
                   setAlert(false);
                   setAlertdata("");
                   history.push('/registerP');
               }} severity="error">{alertdata}</Alert>
               </Stack>
           
           )}
       
               
                <div style={{ marginTop: '10px'}} className="createPostPage">
                   {/* <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>                     */}
                 {/* {({setFieldValue}) => ( */}
                        <Form style={{marginTop: '1000px', color:'white', backgroundColor:red[700] }} className={classes.form} onSubmit={formik.handleSubmit}>
                            
                        <TextField
                            required
                            // id="outlined-required"
                            label="Cnic"
                            name="cnic"
                            value={formik.values.cnic}
                            onChange={formik.handleChange}
                            error={formik.touched.cnic && Boolean(formik.errors.cnic)}
                            helperText={formik.touched.cnic && formik.errors.cnic}
                           placeholder="eg: xxxxxxxxxxx"
                        />

                           
                            {/* <ErrorMessage name="cnic" component="span" style={{color:'whitesmoke'}} />
                            <Field className={classes.input} autoComplete="off" name="cnic" placeholder="eg: xxxxxxxxxxxxxxxx" /> */}

                            {/* <label>Password:</label>
                            <ErrorMessage name="password" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="password" placeholder="eg: @!#@aman " />

                            <label>First Name:</label>
                            <ErrorMessage name="fname" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="fname" placeholder="eg: aman" />


                            <label>Last Name:</label>
                            <ErrorMessage name="lname" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="lname" placeholder="eg: nadeem " />

                            <label>Blood Group:</label>
                            <ErrorMessage name="blood_group" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="blood_group" placeholder="eg: A+ " />

                            <label>Weight:</label>
                            <ErrorMessage name="weight" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="weight" placeholder="eg: 60 in kgs" />

                            <label>Frequency Donation:</label>
                            <ErrorMessage name="frequencyDonation" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="frequencyDonation" placeholder="eg: 2 " />

                            <label>IsDiabetes?</label>
                            <ErrorMessage name="IsDiabetes" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="IsDiabetes" placeholder="eg: yes or no " />

                            <label>IsHypetitus?</label>
                            <ErrorMessage name="IsHypetitus" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="IsHypetitus" placeholder="eg: yes or no" />

                            <label>contact no:</label>
                            <ErrorMessage name="contactno" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="contactno" placeholder="eg: 03002134560" />

                            <label>Postal Code:</label>
                            <ErrorMessage name="postalCode" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="postalCode" placeholder="eg: 97865" />

                            <label>Area:</label>
                            <ErrorMessage name="Area" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="Area" placeholder="eg: nazimabad " />

                            <label>Block:</label>
                            <ErrorMessage name="Block" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="Block" placeholder="eg: B " />


                            <label>City:</label>
                            <ErrorMessage name="City" component="span"  style={{color:'whitesmoke'}}/>
                            <Field autoComplete="off" className={classes.input} name="City" placeholder="eg: Karachi " /> */}
                            

                            {/* <div>
                            <label>Choose a file:</label>
            
                            <input
                                className={classes.input}
                                autoComplete="off"
                                type="file"
                                name="file"
                                accept="/*"
                                // value={}
                                onChange={(event) => {
                                    setFieldValue("file", event.currentTarget.files[0]);
                                    // let reader = new FileReader();
                                    // reader.onload = () =>{
                                    //     if(reader.readyState === 2){
                                    //         setFieldValue('file', reader.result);
                                            
                                    //     }
                                    // }
                                    // reader.readAsDataURL(event.target.files[0]);

        
                                }}


                            />
                           
                           </div> */}
                                                

                            <Button variant="contained" type="submit" style={{ backgroundColor:'whitesmoke', color:red[700] }}>Register</Button>

                        </Form>
                   

                </div>
             
            </Container>
        </Box>
    )

}

export default RegisterP;
