import axios from "axios";
import React, {useEffect, useState, useHistory} from "react";
import { toast } from "react-toastify";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'

function Donate(){
    
    const[bankNameList, setBankNameList] = useState([])
    const[dataset, setData] = useState("")
    const [req, setReq] = useState("")
 


   
        function getBank(){
       
        axios.get('http://localhost:3001/api/Donation/donate', {
            headers:{
                accessToken2: sessionStorage.getItem('accessToken2'),
            }
        }).then((response)=>{
            if(response.data.error){
                console.log('error in donate');
                toast.error(response.data.error)
            }else{
                console.log(response.data);
                setBankNameList(response.data)
                // toast.success(response.data)
            }
            
        })
       
    }

const handleChange = (event)=>{
            // const newdata = {...dataset}
            // newdata[event.target.id] =event.target.value;

            // setData(newdata)
            // console.log(newdata);
       setData(event.target.value)

}
const handleChange2 = (event) => {
    setReq(event.target.value)
}
function Submit(event){
    event.preventDefault()
  
    
    // add the submit api
    axios.post('http://localhost:3001/api/Donation/postdonate',{
        bankName: dataset,
        req_id : req,
    },{
        headers:{
            accessToken2: sessionStorage.getItem('accessToken2'),
        }
    }).then((response)=>{
        if(response.data.error){
            toast.error(response.data.error)
        }if(response.data.req_error){
            toast.error(response.data.req_error)
        }
        else{
            console.log('donate success');
        }
    })
    
    
}

     // function ends
    useEffect(()=>{

        getBank()
    },[])
    return (
        <form onSubmit={(e)=>{Submit(e)}}>
            <h1>Enter Requirement ID: </h1>
            <input type="text" id="req_id" value={req.req_id} onChange={handleChange2}/>
                <h1>Choose Blood Bank</h1>
                <select value={dataset.bankName} onChange={handleChange}>
                 <option value="" >Choose Blood Bank</option>
                { 
                        bankNameList.map((val, key)=>{
                        return(
                            <option value={val.BankName} > {val.BankName}</option>
                                )
                            }
                            )
                    }
                               
            </select>
           <button type="submit">Submit</button>
        </form>
    );


}

export default Donate;
