import React, { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { CircularProgress, Grid } from "@mui/material";
import useStyles from './styles'
import Post from './PostDetail/Post'
const ShowRequests =  () => {


    const [requestList, setRequestList] = useState([])
    const classes = useStyles();
    const user = sessionStorage.getItem('profile');
   useEffect(()=>{
        axios.get('http://localhost:3001/api/Request/requests', {
            headers:{
                accessToken : sessionStorage.getItem("accessToken"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
                
            }else{
            
           setRequestList(response.data);
        }
        })
        console.log(requestList);
    },[])


    return (
        <Grid item xs={12} sm={6} md={9}>
         
        <Grid className={classes.mainContainer} container alignItems="stretch" spacing={3}>
           
        {
            requestList.map((post)=>(
         
             <Grid key={post?.req_id} item xs={12} sm={12} md={6} lg={3}>
                  <Post post={post}/>
              </Grid>
            )
            )
       
        
        }
        </Grid>
        </Grid>
   
    
    )
};

export default ShowRequests;
