// this will return the current users posts 

import axios from "axios";
import React, {useState} from "react";
import { toast } from "react-toastify";

function Myrequests(){
    const [requestList, setRequestList] = useState([])
  
    const myreq = ()=>{
        axios.get("http://localhost:3001/api/Request/myrequests", {
            headers:{
                accessToken2: sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
            }if(response.data.err){
                toast.error('No request exist')
            }
            else if(response.data){
                console.log(response.data);
                setRequestList(response.data)
            }
        })

    }  // inner function ends 
    const onDel = ()=>{
        axios.delete("http://localhost:3001/api/Request/delete", {
            headers:{
                accessToken2: sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('Post is not deleted successfully')
            }else{
                console.log("deleted successfully");
                toast.success("Post deleted successfully")
            }
        })
    }
    return (

        <div>
            <div id="main1">
            <div id="heading">
                <h1 >My Requests</h1>
            </div>
            <button id="reqbtn" onClick={myreq}><h3>Find</h3></button>
            
            </div>
        {
            requestList.map((val,key)=>{
                return (
                    
                    <div className="requests">
                        
                        <div>
                            <div className="main">
                            <h1>Cnic : {val.cnic}</h1>
                            <h1 id="reqid">{val.req_id}</h1>
                            <button id="btn-del" onClick={onDel}>Delete</button>
                            <button id="btn-update">Update</button>
                            </div>
                            <h3>Blood Group: {val.bloodGroup}</h3>
                            <h3>Quantity Needed: {val.Qtyneed}</h3>
                            <h3>Posted At: {val.Date}</h3>
                            <h3>Time: {val.Time}</h3>
                            <h3>Reason: {val.Reason}</h3>
                            <h3>Time Period : {val.TimePeriod}</h3>


                        </div>

                    </div>
                );
            })
        }
       
        </div>
    );


}

export default Myrequests;
