import React, { useEffect, useState } from "react";
import axios from "axios";
import Donor from "./Donor";
import { AppBar, Box, Divider, Grid, IconButton, Toolbar, Typography } from "@mui/material";
import useStyles from './styles';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import { useHistory } from "react-router-dom";


const Donors = ({id, bank}) =>{
    const navigate = useHistory();
    const classes = useStyles();
    const [donors, setDonors] = useState([]);
    const [count, setCount] = useState(0);
   
    useEffect(()=>{

        axios.get(`http://localhost:3001/api/Donation//getDonors/${id}`, {
            headers:{
                accessToken2 : sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                console.log(response.data.error);
                
            }else{
                    console.log(response.data);
                    setDonors(response.data);
           
                 }
        })
        setCount(donors.length);
    }, [])

    return (
        
        <Grid style={{marginTop:'50px'}} className={classes.mainContainer} container alignItems="stretch" spacing={3}>
        {
            donors.map((donor) => (
            <Grid key={donor?.req_id} item xs={12} sm={12} md={6} lg={3}>
                <div style={{marginBottom:'30px', backgroundColor:"crimson", borderRadius:'5px', color:'white'}}>
                <Typography variant="h5">{"Requirement ID: " + id}</Typography>
                <Typography variant="h5">{"Total Donations: " + count}</Typography>
                </div>
                    <Donor id={donor?.req_id} cnic={donor?.cnic} count={count} bank={bank}/>
            </Grid>
            ))
        }
  
        </Grid>
    
    )
}

export default Donors;
