import React, {useState} from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'
import axios  from "axios";
import {Link, useHistory } from "react-router-dom";
import { AppBar, Button, IconButton, Box,Toolbar } from "@mui/material";
import HomeRoundedIcon from'@mui/icons-material/HomeRounded';
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
  }

function RegisterP (){
    let history = useHistory()

    const [error, setError] = useState("");
    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
      );
    
      useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
      
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
      }, []);

   
    // cnic, password, fname, lname, blood_group, weight, frequencyDonation, IsDiabetes, IsHypetitus, contactno,  postalCode, Area, Block, City
    const initialValues = {
        cnic:"",
        password:"",
        fname:"",
        lname:"",
        blood_group:"",
        weight:"",
        frequencyDonation:"",
        IsDiabetes:"",
        IsHypetitus:"",
        contactno:"",
        postalCode:"",
        Area:"",
        Block:"",
        City:""


    }
    const validationSchema = Yup.object().shape({

        cnic: Yup.number().required('The cnic is required must be 13 digits'),
        password: Yup.string().min(10).required('The password is required'),
        fname: Yup.string().required('The first name is required'),
        lname: Yup.string().required('The last name is required'),
        blood_group : Yup.string().min(2).max(2).required('the blood group is required'),
        weight : Yup.number().required('the weight is required 11 digits'),
        frequencyDonation: Yup.number().required('The frequency donation is required'),
        IsDiabetes: Yup.string().required('This is the required field'),
        IsHypetitus: Yup.string().required('This is the required field'),
        contactno: Yup.string().required('The contact number is required'),
        postalCode: Yup.string().required('The postal code is required'),
        Area: Yup.string().required('The area is rquired'),
        Block: Yup.string().required('The block is required'),
        City :Yup.string().required('The city is required'),
        
    });
    const onSubmit = (data)=>{

        axios.post("http://localhost:3001/api/authPerson/RegisterP", data).then((response)=>{

            if(response.data.error){
                console.log(response.data.error);
                console.log("agye isme");
                history.push("/invalidcnic")
                function start(){
                const timeoutStart = setTimeout(()=>{
                   history.push("/registerP")
                }, 3000)
            }
                start()
            }  else{
                // if success 
                // alert(response.data)
                console.log('agya isme2');
                console.log(response.data);
                history.push("/success")
                function start(){
                const now = setTimeout(() =>{
                    history.push("/loginP")
                },3000)
            }
                start()
        }
        
            

        })

        
        
    }
  
    return ( 
        <Box  sx={{ flexGrow: 1 }}>
   
        <AppBar style={{backgroundColor:'crimson'}} position="static">
        
          <Toolbar>
          <IconButton
              size="large"
              edge="false"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={()=>{
                history.push('/');
              }}
           
            >
              <HomeRoundedIcon />
            </IconButton>
            <Button style={{marginLeft: windowDimensions.width <= 1058 ? '40px' : '250px', textDecoration: 'underline'}} component={Link}  color="inherit" > Blood Bank?</Button>
            <Button style={{marginLeft: windowDimensions.width <= 1058 ? '40px' : '250px', textDecoration: 'underline'}} component={Link} to='/user' color="inherit" >Donor or Needy?</Button>
            </Toolbar>
        </AppBar> 
    
        <div className="createPostPage">

<Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>

<Form className="formContainer">
<div className="header">User Registration </div>

<label>Cnic:</label>
<ErrorMessage name="cnic" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" name="cnic" placeholder="eg: xxxxxxxxxxxxxxxx"/>

<label>Password:</label>
<ErrorMessage name="password" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" name="password" placeholder="eg: @!#@aman "/>

<label>First Name:</label>
<ErrorMessage name="fname" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" name="fname" placeholder="eg: aman"/>


<label>Last Name:</label>
<ErrorMessage name="lname" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" name="lname" placeholder="eg: nadeem "/>

<label>Blood Group:</label>
<ErrorMessage name="blood_group" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" name="blood_group" placeholder="eg: A+ "/>

<label>Weight:</label>
<ErrorMessage name="weight" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="weight" placeholder="eg: 60 in kgs"/>

<label>Frequency Donation:</label>
<ErrorMessage name="frequencyDonation" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="frequencyDonation" placeholder="eg: 2 "/>

<label>IsDiabetes?</label>
<ErrorMessage name="IsDiabetes" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="IsDiabetes" placeholder="eg: yes or no "/>

<label>IsHypetitus?</label>
<ErrorMessage name="IsHypetitus" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="IsHypetitus" placeholder="eg: yes or no"/>

<label>contact no:</label>
<ErrorMessage name="contactno" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="contactno" placeholder="eg: 03002134560"/>

<label>Postal Code:</label>
<ErrorMessage name="postalCode" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="postalCode" placeholder="eg: 97865"/>

<label>Area:</label>
<ErrorMessage name="Area" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="Area" placeholder="eg: nazimabad "/>

<label>Block:</label>
<ErrorMessage name="Block" component="span"/>
<Field autoComplete = "off" id="inputCreatePost"  name="Block" placeholder="eg: B "/>


<label>City:</label>
<ErrorMessage name="City" component="span"/>
<Field autoComplete = "off" id="inputCreatePost" className="post" name="City" placeholder="eg: Karachi "/>

<button type="submit">Register</button>




</Form>

</Formik>

</div>
        
        </Box>
      )
    
    
    
    

}

export default RegisterP;
