import axios from "axios";
import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
function PersonInfo (){

    const [requestList, setRequestList] = useState([])
    
    const [requestList2, setRequestList2] = useState([])
    const [requestList3, setRequestList3] = useState([])


    const show = ()=>{
        axios.get('http://localhost:3001/api/Person/personinfo', {
            headers:{
                accessToken2 : sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                toast.error('You are not authenticated to use this feature')
                
            }else{
            
           setRequestList(response.data);
        //    console.log(requestList[0].loc_id);
                const show2 = ()=>{
                        axios.get('http://localhost:3001/api/Person/personcontact', {
                            headers:{
                                accessToken2 : sessionStorage.getItem("accessToken2"),
                            }
                        }).then((response)=>{
                            if(response.data.error){
                                toast.error('You are not authenticated to use this feature')
                                
                            }else{
                            
                           setRequestList2(response.data);

                           const show3= ()=>{
                            axios.get(`http://localhost:3001/api/Person/personloc/${requestList[0].loc_id}`, {
                                headers:{
                                    accessToken2 : sessionStorage.getItem("accessToken2"),
                                }
                            }).then((response)=>{
                                if(response.data.error){
                                    toast.error('You are not authenticated to use this feature')
                                    
                                }else{
                                
                               setRequestList3(response.data);
                                }
                            })
                    }// show3 ends
                        show3()
                            }
                        })
                }// show2 ends
                show2()

        }
        })
    
    }
    return (

        <div>
            <div id="main1">
            <div id="heading">
                <h1 >Personal Details</h1>
            </div>
            <button id="reqbtn" onClick={show}><h3>Find</h3></button>
            
            </div>
        {
            requestList.map((val,key)=>{
                return (
                    
                    <div className="requests">
                        
                        <div>
                            <div className="main">
                            <h1>Cnic : {val.cnic}</h1>
                            <h1 id="reqid">Blood Group: {val.blood_group}</h1>
                            </div>
                            <h3>Name: {val.fname + " " + val.lname}</h3>
                            <h3>Weight: {val.weight}</h3>
                            <h3>no. of time donate: {val.frequencyDonation}</h3>
                            <h3>IsDiabetes?: {val.IsDiabetes}</h3>
                            <h3>IsHypetitus?: {val.IsHypetitus}</h3>
                            {
                                requestList2.map((val2,key)=>{
                                    return(
                                            <h3>ContactNo{(key+1) + ": 0" +val2.contactno}</h3>
                                    );
                                })
                            }
                        </div>

                    </div>
                );
            })
        }
       
        </div>
    );




}


export default PersonInfo;
