import axios from "axios";
import React, {useEffect, useState} from "react";
import { toast } from "react-toastify";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'

function Donate(){
    const[bankNameList, setBankNameList] = useState([])
    const[bankName, setBankName] = useState("")
    const getBanks = ()=>{
       
    axios.get('http://localhost:3001/api/Donation/donate', {
        headers:{
            accessToken2: sessionStorage.getItem('accessToken2'),
        }
    }).then((response)=>{
        if(response.data.error){
            console.log('error in donate');
            toast.error(response.data.error)
        }else{
            console.log(response.data);
            setBankNameList(response.data)
            toast.success(response.data)
        }
        
    })
}// function ends
useEffect(()=>{
    getBanks()
}, [])

const handleChange = (event)=>{
            setBankName(event.target.value)

}
const save = (event)=>{
    event.preventDefault()
    // add the submit api
    console.log(bankName);
}
    return ( 
    
    <div>
    <h1>Choose Blood Bank</h1>
    <select value={bankName} onChange={handleChange}>
        <option value="" >Choose Blood Bank</option>

        {
            //   <option>{C2}</option>
           bankNameList.map((val, key)=>{
            return(
            <option value={val.BankName}id={key+1}> {val.BankName}</option>
            )
           })
        }
       
    </select>
    <button onClick={save}>Save</button>
    </div>
    
    )

}

export default Donate;
