import React,{useState} from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup'
import axios from 'axios'
import { toast } from "react-toastify";
import useStyles from './styles';
import { TextField, Typography, Button, Paper, Stack, Alert} from "@mui/material";

function CreateRequest (){
    const classes  = useStyles();
    const user =  sessionStorage.getItem('profile');
    const [postData, setPostData] = useState({
        bloodGroup:"",
        QtyNeed:"",
        Reason:"",
        TimePeriod:"",  
    })
    const [alert, setAlert] = useState(false);
    const [alertData, setAlertData] = useState("");

    // const validationSchema = Yup.object().shape({
    //     // cnic : Yup.number().required('The cnic is required'),
    //     bloodGroup: Yup.string().required('The blood group is required'),
    //     QtyNeed: Yup.number().required('The qty is required'),
    //     // Date : Yup.string().required('The date is required'),
    //     // .max(new Date(), 'You cant be be born in a future'),
    //     // Time: Yup.string().required('the time is required'),
    //     TimePeriod : Yup.number().required('The time period is required'),
    //     Reason: Yup.string().min(10).required('The reason is required'),
    // });
    // {cnic : cnic, bloodGroup: bloodGroup, QtyNeed: QtyNeed, Date : Date, Time : Time, TimePeriod : TimePeriod, Reason : Reason 
    const handleSubmit = (e)=>{
        e.preventDefault();


        // if creating a post 
        axios.post('http://localhost:3001/api/Request/create', postData,{
            headers:{
                accessToken2 : sessionStorage.getItem('accessToken2'),
            }
        }).then((response)=>{
            if(response.data.error){
                setAlert(true);
                setAlertData(response.data.error);
            }else{
            console.log(response.data);

            }
        })
        clear();

    }
    const clear = () =>{
        setPostData({
            bloodGroup : "",
            timePeriod : "",
            QtyNeed:"",
            Reason:"",
        })
    }
    return (

        

        <div>
             {alert && (
               <div>
               <Stack sx={{width:'100%', marginBottom:'20px'}}>
               <Alert onClose={()=>{
                   setAlert(false);
                   setAlertData("");
               }} severity="error">{alertData}
               </Alert>
               </Stack>
               </div>
           )}
       
           
            <form autoComplete="off" validate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
            {/* {user ? 'Editing' : 'Creating'} a Post */}
            <Typography variant="h6">Creating a Post</Typography>
            <TextField
            name="bloodGroup"
            variant="outlined"
            fullWidth
            label="Blood Group"
            required
            value={postData.bloodGroup}
            onChange={(e) => setPostData({...postData, bloodGroup: e.target.value})}
            
            />

        <TextField
            name="Time Period"
            variant="outlined"
            fullWidth
            label="Time Period (days)"
            type="number"
            required
            value={postData.timePeriod}
            onChange={(e) => setPostData({...postData, timePeriod: e.target.value})}
            
            />

        <TextField
            name="QtyNeed"
            variant="outlined"
            fullWidth
            label="Quantity Need"
            type="number"
            required
            value={postData.QtyNeed}
            onChange={(e) => setPostData({...postData, QtyNeed: e.target.value})}
            
            />

        <TextField
            name="Reason"
            variant="outlined"
            fullWidth
            label="Message"
            required
            multiline
            rows={4}
            value={postData.Reason}
            onChange={(e) => setPostData({...postData, Reason: e.target.value})}
            
            />
            
            <Button className={classes.buttonSubmit} variant='contained ' size="large" type="submit" fullWidth >Post</Button>
            <Button variant='contained' className={classes.buttonClear} size="small" onClick={clear} fullWidth >Clear</Button>

            </form>


        </div>
    
    );
    
}
export default CreateRequest;
