import  React,{useEffect, useState} from 'react';
import {styled, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import MuiAppBar, { AppBarProps as MuiAppBarProps } from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import {Container, Grid, Grow} from '@mui/material'
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import LoginIcon from '@mui/icons-material/Login';
import HowToRegIcon from '@mui/icons-material/HowToReg';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import video from '../../assets/video.mp4';
import Avatar from '@mui/joy/Avatar';
import PostAddIcon from '@mui/icons-material/PostAdd';
import SelectAllIcon from '@mui/icons-material/SelectAll';
import BloodtypeIcon from '@mui/icons-material/Bloodtype';
import VolunteerActivismIcon from '@mui/icons-material/VolunteerActivism';
import LogoutIcon from '@mui/icons-material/Logout';
// import profile from '../../images/aman.jpg';
import axios from 'axios';
import {Buffer} from 'buffer';
import CreateRequest from '../Post/CreateRequest';
import ShowRequests from '../Post/ShowRequests';

const drawerWidth = 240;

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })<{
  open?: boolean;
}>(({ theme, open }) => ({
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create('margin', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: `-${drawerWidth}px`,
  ...(open && {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  }),
}));

interface AppBarProps extends MuiAppBarProps {
  open?: boolean;
}

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})<AppBarProps>(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}));

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
      width,
      height,
  };
}

const UserHome = () => {

  const User = sessionStorage.getItem('accessToken2');

  const history = useHistory();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [profile, setProfile] = React.useState("");
  const [fname, setfName] = React.useState("");
  const [lname, setlName] = React.useState("");

  const [windowDimensions, setWindowDimensions] = useState(
    getWindowDimensions()
  );
  useEffect(()=>{
    if(User){
      axios.get("http://localhost:3001/api/Person/personinfo", {
            headers:{
                accessToken2: sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
          if(response.data){
             setProfile(response.data[0].file);
             setfName(response.data[0].fname);
             setlName(response.data[0].lname);
          }
            
        })

    }
  }, [User])
  useEffect(() => {
    function handleResize() {
        setWindowDimensions(getWindowDimensions());
        console.log(windowDimensions.width);
    }
  
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);


  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar  position="fixed" open={open} style={{backgroundColor:'crimson', height:'70px'}}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div">
            Bloodu Donor/Needy
          </Typography>
         { User && (
          <div style={{ 
        position: "absolute", 
        top: "10px",
        right: "30px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        }} >
         
          <Avatar alt='Profile' onClick={()=>{
          history.push('/myinfo')
        }} src={profile}/>
        <Typography style={{color:"white"}} variant='caption'>{fname.toUpperCase()} {lname.toUpperCase()} </Typography>
         
        </div>
      
        )}
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </DrawerHeader>
        <Divider />
        <List>
            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <HomeRoundedIcon/>
                </ListItemIcon>
                <ListItemText primary='Home' />
              </ListItemButton>
            </ListItem>
            { !User && (
              <div>
            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/registerP")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <HowToRegIcon/>
                </ListItemIcon>
                <ListItemText primary='Register Account' />
              </ListItemButton>
            </ListItem>

            <ListItem  disablePadding >
              <ListItemButton onClick={()=>{
                  history.push("/loginP")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <LoginIcon/>
                </ListItemIcon>
                <ListItemText primary='Login'  />
              </ListItemButton>
            </ListItem>
            </div>
            )}

        {User && (
            <div>
              <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/createrequest")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <PostAddIcon/>
                </ListItemIcon>
                <ListItemText primary='Create Post' />
              </ListItemButton>
            </ListItem>

            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/myrequests")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <SelectAllIcon/>
                </ListItemIcon>
                <ListItemText primary='My Posts' />
              </ListItemButton>
            </ListItem>

            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/donate")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <BloodtypeIcon/>
                </ListItemIcon>
                <ListItemText primary='Donate Now' />
              </ListItemButton>
            </ListItem>
            
            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                  history.push("/donations")
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <VolunteerActivismIcon/>
                </ListItemIcon>
                <ListItemText primary='Donations' />
              </ListItemButton>
            </ListItem>

            <ListItem  disablePadding>
              <ListItemButton onClick={()=>{
                sessionStorage.removeItem('accessToken2');
                history.push('/user');
              }}> 
                <ListItemIcon style={{color:'crimson'}}>
                  <LogoutIcon/>
                </ListItemIcon>
                <ListItemText primary='Logout' />
              </ListItemButton>
            </ListItem>

            </div>  
          )}
        </List>
        <Divider/>
      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        
        {/* className={ classes.gridContainer } */}
        <Grow in>
            <Container maxWidth="xl">
                <Grid container 
               
                direction={windowDimensions.width <= 600 ? 'column-reverse' : 'row'}
                    justifyContent="space-between"
                     alignItems="stretch" 
                     spacing={3}
                    >
                    <Grid item xs={12} sm={6} md={9}>
                       <ShowRequests />
                    </Grid>

                    <CreateRequest/>
              </Grid>
              </Container>
          </Grow>


       
         
           
     


      </Main>
    </Box>
  );
}
export default UserHome;
