import React, {useEffect, useState} from 'react'
import axios from 'axios'
import { AppBar, Box, ButtonBase, Card, CardContent, CardMedia, IconButton, Toolbar, Typography } from '@mui/material'

const  Donor = ({cnic, count, bank}) => {
    const [person, setPerson] = useState({})
  useEffect(()=>{
    
    axios.get(`http://localhost:3001/api/Person/person/${cnic}`, {
        headers:{
            accessToken2 : sessionStorage.getItem("accessToken2"),
        }
    }).then((response)=>{
        if(response.data.error){
            console.log(response.data.error);            
        }else{
            console.log(response.data);
            setPerson(response.data[0]);
        }
    })
  }, [])
  
    return (

        <Box  sx={{ flexGrow: 1 }}>
   
        <AppBar style={{backgroundColor:'crimson'}} position="static">
        
          <Toolbar>
          <IconButton
              size="large"
              edge="false"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={()=>{
                navigate.push('/user');
              }}
           
            >
              <HomeRoundedIcon style={{height:'50px', width:'50px'}}/>
            </IconButton>
            <Typography variant="h1" >Personal Details</Typography>
            
            </Toolbar>
           
        </AppBar> 
        
        <Card className={classes.card} raised elevation={6} >
        <ButtonBase className={classes.cardAction} onClick={openPost}>
          <CardMedia className={classes.media} image={post.selectedFile} />
          <div className={classes.overlay}>
            <Typography variant='h6'>{post.name}</Typography>
            <Typography variant='body2'>{moment(post.createdAt).fromNow()}</Typography>
          </div>
         
          <div className={classes.details}>
            <Typography variant='body2' color="textSecondary">{post.tags.map((tag) => `#${tag} `)}</Typography>
          </div>
          <Typography className={classes.title} variant='h5' gutterBottom>{post.title}</Typography>
          <CardContent>
            <Typography variant='body2' color="textSecondary" component="p" >{post.message}</Typography>
          </CardContent>
  
        </ButtonBase>
  
        <CardActions className={classes.cardActions}>
          {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator) && (
  
            <div className={classes.overlay2}>
              <Button
                style={{ color: 'white' }}
                size='small'
                onClick={() => setCurrentId(post._id)}>
                <MoreHorizIcon/>
              </Button>
            </div>
          )}
  
  
          <Button size='small' color='primary' disabled={!user?.result} onClick={handleLike}>
            <Likes />
          </Button>
          {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator) && (
  
            <Button size='small' color='primary' onClick={() =>
  
              dispatch(deletePost(post._id))
            }>
              <DeleteIcon fontSize='small' />
              Delete
            </Button>
          )}
  
        </CardActions>
      </Card>
      </Box>
  
  )
}

export default Donor;
