import React, {useState} from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography} from '@mui/material'
import useStyles from './postStyles';
import DeleteIcon from '@mui/icons-material/Delete';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz'

const Post = ({post}) => {
    const classes = useStyles();

    return (
        <Card className={classes.card} raised elevation={6}>
            <CardMedia className={classes.media} image={post?.profile}/>
            <div className={classes.overlay}>
          <Typography variant='h6'>ID: {post?.req_id}</Typography>
          <Typography variant='body2'>Posted: {`${post?.Date} ${post?.Time}`}</Typography>
        
        </div>

        <Typography className={classes.title} variant='h5' gutterBottom>{post?.cnic}</Typography>
        <Typography  variant='body2' gutterBottom>Group: {post?.bloodGroup}</Typography>
        <Typography  variant='body2' gutterBottom>Bottles Needed: {post?.QtyNeed}</Typography>
        <Typography  variant='body2' gutterBottom>Time Period: {post?.timePeriod} days</Typography>


        <CardContent>
          <Typography variant='body2' color="textSecondary" component="p" >{post?.Reason}</Typography>
        </CardContent>
            
        </Card>
    )

}

export default Post;
