import React, { useEffect, useState } from "react";
import axios from "axios";
import Donor from "./Donor";
import { AppBar, Box, Grid, IconButton, Toolbar, Typography } from "@mui/material";
import useStyles from './styles';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import { useHistory } from "react-router-dom";


const Donors = ({id, bank}) =>{
    const navigate = useHistory();
    const classes = useStyles();
    const [donors, setDonors] = useState([]);
    const [count, setCount] = useState(0);
   
    useEffect(()=>{

        axios.get(`http://localhost:3001/api/Donation//getDonors/${id}`, {
            headers:{
                accessToken2 : sessionStorage.getItem("accessToken2"),
            }
        }).then((response)=>{
            if(response.data.error){
                console.log(response.data.error);
                
            }else{
                    console.log(response.data);
                    setDonors(response.data);
           
                 }
        })
        setCount(donors.length);
    }, [])

    return (
        <Box  sx={{ flexGrow: 1 }}>
   
        <AppBar style={{backgroundColor:'crimson'}} position="static">
        
          <Toolbar>
          <IconButton
              size="large"
              edge="false"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={()=>{
                navigate.push('/user');
              }}
           
            >
              <HomeRoundedIcon style={{height:'50px', width:'50px'}}/>
            </IconButton>
            <Typography variant="h1" >Donors</Typography>
            
            </Toolbar>
           
        </AppBar> 
        
        <Grid style={{marginTop:'50px'}} className={classes.mainContainer} container alignItems="stretch" spacing={3}>
        {
            donors.map((donor) => (
            <Grid key={donor?.req_id} item xs={12} sm={12} md={6} lg={3}>
                <div style={{marginBottom:'30px'}}>
                <Typography variant="h2">{"Requirement ID: " + id}</Typography>
                <Typography variant="h2">{"Total Donations: " + count}</Typography>
                </div>
                    <Donor id={donor?.req_id} cnic={donor?.cnic} count={count} bank={bank}/>
            </Grid>
            ))
        }
        </Grid>
        
        </Box>
    )
}

export default Donors;
